import React from "react";
import ReactDOM from "react-dom";
import classNames from "classnames";
import { v1 as uuidv1 } from 'uuid'

import "./index.scss";

class App extends React.Component {
    state = {
        notes: [ ]
    };

    handleAddNote = texto => {
       
        this.setState(prevState =>( {
            
            notes: prevState.notes.concat({id: uuidv1() , texto}) }));
    }

    handleMove = (direction, index) =>{
        this.setState(preventState => {
        const newNotes = preventState.notes.slice();
        const removeItemNode = newNotes.splice(index, 1)[0];

        if(direction === "up"){
          newNotes.splice(index -1, 0, removeItemNode);  
        }else{
            newNotes.splice(index +1, 0, removeItemNode);   
        }

        return { notes: newNotes};
    })
    }

    render(){
        return(
            <div className="container">
                <NewNote onAddNote = {this.handleAddNote}/>
                <NoteList  notes={this.state.notes} onMove={this.handleMove}/>
            </div>
        )
    }
}

class NewNote extends React.Component{
    state = {
        text:""
    }
    render(){
        const {onAddNote} = this.props;
        const { text} = this.state;
        return (
            <div className="caixaNote">
            <input type="text" className="note"
             placeholder="digite sua nota"
             value ={text}
             onChange={ event => {
                 this.setState({
                     text: event.target.value
                 })
             }}
             onKeyPress = { event => {
                if(event.key === "Enter"){
                    onAddNote(event.target.value);
                    this.setState({text:""});
                }
            }}
            />
        </div>
        )
    }
}
 

const NoteList = ({notes, onMove}) =>(
    <div  className="lista">
    {notes.map((note, index) => <div key = {note.id} className="item">
    <span className="node_text">{note.texto}</span>
    <button 
    className={classNames("note_button", {"note_button--hidden":index === 0} )}

    onClick={() => { onMove("up", index)}}
    >
    <i className="material-icons">
        keyboard_arrow_up
    </i>
    </button>
    <button   className={classNames("note_button",{"note_button--hidden":index === notes.length -1} )}
    onClick={() => { onMove("dow", index)}}
    >
    <i className="material-icons">
    keyboard_arrow_down
    </i>
    </button>
    </div>)}               
    </div>
)

ReactDOM.render(
 <App />,
 document.getElementById("root")

)
